import React from 'react';
import './Carousel.css';

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {}
  }
  render() {
    let style = {};
    let content;

    if(this.props.settings.style) {
      style = {width: `${this.props.settings.style.width}px`, height: `${this.props.settings.style.height}px`}
    }

    if(this.props.items.length > 0) {
      content = this.props.items.map((value, key) => (
        <div className='carousel_element' key={key} style={style}></div>
      ))
    }

    return (
      <div style={style} className='carousel_inner'>
        <div className='carousel_wrapper'>
          {content}
        </div>
      </div>
    )
  }
}

export default Carousel
