import React from 'react';
import './Carousel.css';


function CarouselItem(props) {
  return (
    <div className='carousel_element' style={props.style}>
      {props.children}
    </div>
  );
}

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.animation = this.animation.bind(this);
    this.default_settings = {
      style: {
        width: 400,
        height: 240
      },
      elemRender: 1,
      seconds: 1000,
      loop: true
    };
    this.state = {current: 1, translateX: 0, styles: {}};
  }

  animation() {
    let new_current;
    let new_translateX;
    if(this.state.current <= (this.props.items.length - this.props.settings.elemRender)) {
      new_current = this.state.current + this.props.settings.elemRender;
      new_translateX = this.state.translateX - (this.props.settings.style.width * this.props.settings.elemRender)
    } else {
      new_current = 1;
      new_translateX = 0;
    }

    let newStateStyle = {
      inner: this.state.styles.inner,
      wrapper: {
        width: this.props.settings.style.width * this.props.items.length,
        height: this.props.settings.style.height,
        transform: `translateX(${new_translateX}px)`
      },
      elements: this.state.styles.elements
    };

    this.setState(
      {
        current: new_current,
        translateX: new_translateX,
        styles: newStateStyle
      }
    )
  }

  componentDidMount() {
    if('settings' in this.props) {
      this.setState({
        styles: {
          inner: {
            width: this.props.settings.style.width * this.props.settings.elemRender,
            height: this.props.settings.style.height
          },
          wrapper: {
            width: this.props.settings.style.width * this.props.items.length,
            height: this.props.settings.style.height,
            transform: "translateX(0px)"
          },
          elements: {
            width: this.props.settings.style.width,
            height: this.props.settings.style.height
          },
        }
      });
      if('autoPlay' in this.props.settings && this.props.settings.autoPlay === true) {
        this.interval = setInterval(this.animation, this.props.settings.seconds);
      } 
    } else {
      this.setState({
        styles: {
          inner: {
            width: this.default_settings.settings.style.width * this.default_settings.settings.elemRender,
            height: this.default_settings.settings.style.height
          },
          wrapper: {
            width: this.default_settings.settings.style.width * this.default_settings.items.length,
            height: this.default_settings.settings.style.height,
            transform: "translateX(0px)"
          },
          elements: {
            width: this.default_settings.settings.style.width,
            height: this.default_settings.settings.style.height
          },
        }
      });
    }
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    let styleInner = {};
    let styleWrapper = {};
    let styleElement = {};
    let content;

    console.log(this.state)

    if('inner' in this.state.styles) styleInner = this.state.styles.inner;
    if('wrapper' in this.state.styles) styleWrapper = this.state.styles.wrapper;
    if('elements' in this.state.styles) styleElement = this.state.styles.elements;

    if(this.props.items.length > 0) {
      content = this.props.items.map((value, key) => (
        <CarouselItem key={key} style={styleElement}>
          <h1>{value.title}</h1>
          <p>{value.text}</p>
        </CarouselItem>
      ))
    }

    return (
      <div style={styleInner} className='carousel_inner'>
        <div style={styleWrapper} className='carousel_wrapper'>
          {content}
        </div>
      </div>
    )
  }
}

export default Carousel
