import React from 'react';
import './Carousel.css';


function CarouselItem(props) {
  return (
    <div className='carousel_element' style={props.style}>
      {props.children}
    </div>
  );
}

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.animation = this.animation.bind(this);
    this.default_settings = {
      style: {
        width: 400,
        height: 240
      },
      elemRender: 1,
      animation: "swap",
      seconds: 1000,
      loop: true
    };
    this.state = {current: 1, translateX: 0, styles: {}};
  }

  animation() {
    let new_current;
    let new_translateX;
    if(this.state.current < this.props.items.length) {
      new_current = this.state.current + this.props.settings.elemRender;
      new_translateX = this.state.translateX - (this.props.settings.style.width * this.props.settings.elemRender)
    } else {
      new_current = 1;
      new_translateX = 0;
    }

    this.setState({current: new_current,translateX: new_translateX, styles: {transform: `translateX(${new_translateX}px)`, width: `${this.props.settings.style.width * this.props.items.length}px`}})
  }

  componentDidMount() {
    this.setState({
      styles: {
        inner: {
          width: this.props.settings.style.width * this.props.settings.elemRender,
          height: this.props.settings.style.height
        },
        wrapper: {
          width: this.props.settings.style.width * this.props.settings.elemRender,
          height: this.props.settings.style.height
        },
        elements: {
          width: this.props.settings.style.width,
          height: this.props.settings.style.height
        },
      }
    })
    // this.interval = setInterval(this.animation, this.props.settings.seconds);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    let styleInner = {};
    let styleWrapper = {};
    let styleElement = {};
    let content;

    if('inner' in this.state.styles) styleInner = this.state.styles.inner;
    if('wrapper' in this.state.styles) styleWrapper = this.state.styles.wrapper;
    if('elements' in this.state.styles) styleElement = this.state.styles.elements;

    if(this.props.items.length > 0) {
      content = this.props.items.map((value, key) => (
        <CarouselItem key={key} style={styleElement}>
          <h1>{value.title}</h1>
          <p>{value.text}</p>
        </CarouselItem>
      ))
    }

    return (
      <div style={styleInner} className='carousel_inner'>
        <div style={styleWrapper} className='carousel_wrapper'>
          {content}
        </div>
      </div>
    )
  }
}

export default Carousel
