import './App.css';
import Carousel from './Carousel';

const elements = [
  {img: false, title: "1 Elem title", text: "1 Elem text"},
  {img: false, title: "2 Elem title", text: "2 Elem text"},
  {img: false, title: "3 Elem title", text: "3 Elem text"},
  {img: false, title: "4 Elem title", text: "4 Elem text"},
  {img: false, title: "5 Elem title", text: "5 Elem text"},
  {img: false, title: "6 Elem title", text: "6 Elem text"},
  {img: false, title: "7 Elem title", text: "7 Elem text"},
  {img: false, title: "8 Elem title", text: "8 Elem text"},
  {img: false, title: "9 Elem title", text: "9 Elem text"}
];

const settings = {
  exampleScroll: {
    width: 400,
    height: 240,
    animation: 'scroll',
    autoPlay: true,
    elementsRender: 3,
    seconds: 3500,
  },
  exampleFade: {
    width: 300,
    height: 180,
    animation: 'fade',
    autoPlay: true,
    elementsRender: 4,
    seconds: 5000,
    secondsFade: 250
  },
  exampleArrows: {
    width: 400,
    height: 240,
    animation: 'scroll',
    autoPlay: false,
    elementsRender: 1,
    seconds: 3500,
    arrows: true
  },
  exampleDots: {
    width: 400,
    height: 240,
    animation: 'scroll',
    autoPlay: true,
    elementsRender: 1,
    seconds: 3500,
    dots: true
  },
};


function App() {
  return (
    <div className="App">
      <Carousel settings={ settings.exampleScroll } elements={ elements } />
      <Carousel settings={ settings.exampleFade } elements={ elements } />
      <Carousel settings={ settings.exampleArrows } elements={ elements } />
      <Carousel settings={ settings.exampleDots } elements={ elements } />
    </div>
  );
}

export default App;
