import React from 'react';
import './Carousel.css';


function CarouselItem(props) {
  return className='carousel_element' (
    <div></div>
  );
}

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.animation = this.animation.bind(this);
    this.default_settings = {
      style: {
        width: 400,
        height: 240
      },
      elemRender: 1,
      animation: "swap",
      seconds: 1000,
      loop: true
    };
    this.state = {current: 1, translateX: 0, styles: {}};
  }

  animation() {
    let new_current;
    let new_translateX;
    if(this.state.current < this.props.items.length) {
      new_current = this.state.current+1;
      new_translateX = this.state.translateX - this.props.settings.style.width
    } else {
      new_current = 1;
      new_translateX = 0;
    }

    this.setState({current: new_current,translateX: new_translateX, styles: {transform: `translateX(${new_translateX}px)`, width: `${this.props.settings.style.width * this.props.items.length}px`}})
  }

  componentDidMount() {
    this.interval = setInterval(this.animation, this.props.settings.seconds);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    let style = {};
    let content;

    if(this.props.settings.style) {
      style = {width: `${this.props.settings.style.width * this.props.settings.elemRender}px`, height: `${this.props.settings.style.height}px`}
    }

    if(this.props.items.length > 0) {
      content = this.props.items.map((value, key) => (
        <div className='carousel_element' key={key} style={style}>
          <h1>{key+1}</h1>
        </div>
      ))
    }

    return (
      <div style={style} className='carousel_inner'>
        <div style={this.state.styles} className='carousel_wrapper'>
          {content}
        </div>
      </div>
    )
  }
}

export default Carousel
