import React from 'react';
import './Carousel.css';

function CarouselElement( props ) {
  return (
    <div className='carousel_element' style={ props.style }>
      { props.children }
    </div>
  );
}

function CarouselDot( props ) {
  return (
    <div className={ props.className }></div>
  )
}

function CarouselArrow( props ) {
  return (
    <div className={ props.className } onClick={ props.handle }></div>
  )
}

class Carousel extends React.Component {
  constructor( props ) {
    super( props );
    this.animationFade = this.animationFade.bind( this );
    this.animationScroll = this.animationScroll.bind( this );
    this.handleArrowPrev = this.handleArrowPrev.bind( this );
    this.handleArrowNext = this.handleArrowNext.bind( this );
    this.default_settings = {
      style: {
        width: 400,
        height: 240
      },
      elemRender: 1,
      seconds: 1000,
    };
    this.state = {
      current: 1,
      translateX: 0,
      styles: {},
      dots: false,
      arrows: false
    };
  }

  animationFade(mode="next") {
    let new_current;
    let new_translateX;

    setTimeout( () => {
      document.querySelector(`.carousel_${this.props.settings.animation}`).style.opacity = "0";
    }, 0 );

    setTimeout( () => {
      document.querySelector(`.carousel_${this.props.settings.animation}`).style.opacity = "1";
    }, 1000 )

    if(this.state.current <= (this.props.items.length - this.props.settings.elemRender)) {
      new_current = this.state.current + this.props.settings.elemRender;
      new_translateX = this.state.translateX - (this.props.settings.width * this.props.settings.elemRender)
    } else {
      new_current = 1;
      new_translateX = 0;
    }

    let newStateStyle = {
      inner: this.state.styles.inner,
      wrapper: {
        width: this.props.settings.width * this.props.items.length,
        height: this.props.settings.height,
        transform: `translateX(${new_translateX}px)`
      },
      elements: this.state.styles.elements
    };

    setTimeout( () => {
      this.setState(
        {
          current: new_current,
          translateX: new_translateX,
          styles: newStateStyle
        }
      )
    }, 1500 )

  }

  animationScroll(mode="next") {
    let new_current;
    let new_translateX;

    if(this.state.current <= (this.props.items.length - this.props.settings.elemRender)) {
      new_current = this.state.current + this.props.settings.elemRender;
      new_translateX = this.state.translateX - (this.props.settings.width * this.props.settings.elemRender)
    } else {
      new_current = 1;
      new_translateX = 0;
    }

    let newStateStyle = {
      inner: this.state.styles.inner,
      wrapper: {
        width: this.props.settings.width * this.props.items.length,
        height: this.props.settings.height,
        transform: `translateX(${new_translateX}px)`
      },
      elements: this.state.styles.elements
    };

    this.setState(
      {
        current: new_current,
        translateX: new_translateX,
        styles: newStateStyle
      }
    )
  }

  handleArrowPrev() {
    clearInterval( this.interval );
  }

  handleArrowNext() {
    clearInterval( this.interval );
  }

  componentDidMount() {
    let new_state;

    if('settings' in this.props) {
      new_state = {
        seconds: this.props.settings.seconds,
        elemRender: this.props.settings.elemRender,
        styles: {
          inner: {
            width: this.props.settings.width * this.props.settings.elemRender,
            height: this.props.settings.height
          },
          wrapper: {
            width: this.props.settings.width * this.props.items.length,
            height: this.props.settings.height,
            transform: "translateX(0px)"
          },
          elements: {
            width: this.props.settings.width,
            height: this.props.settings.height
          }
        }
      };

      if('dots' in this.props.settings) {
        new_state.dots = this.props.settings.dots;
      };

      if('arrows' in this.props.settings) {
        new_state.arrows = this.props.settings.arrows;
      };

      if('autoPlay' in this.props.settings && this.props.settings.autoPlay === true) {
        if('animation' in this.props.settings) {
          console.log(this.props.settings.animation)
          if(this.props.settings.animation === 'scroll') {
            this.interval = setInterval(this.animationScroll, this.props.settings.seconds);
          } else if(this.props.settings.animation === 'fade') {
            this.interval = setInterval(this.animationFade, this.props.settings.seconds);
          }
        }
      };
    } else {
      new_state = {
        seconds: this.default_settings.seconds,
        elemRender: this.default_settings.elemRender,
        styles: {
          inner: {
            width: this.default_settings.settings.style.width * this.default_settings.settings.elemRender,
            height: this.default_settings.settings.style.height
          },
          wrapper: {
            width: this.default_settings.settings.style.width * this.default_settings.items.length,
            height: this.default_settings.settings.style.height,
            transform: "translateX(0px)"
          },
          elements: {
            width: this.default_settings.settings.style.width,
            height: this.default_settings.settings.style.height
          },
        }
      };
    }
    this.setState(new_state)
  }

  componentWillUnmount() {
    clearInterval( this.interval );
  }

  renderContent() {
    let styleElement = {};

    if('elements' in this.state.styles) {
      styleElement = this.state.styles.elements;
    }

    if(this.props.items.length > 0) {

      return this.props.items.map( ( value, key ) => (
        <CarouselElement key={ key } style={ styleElement }>
          <h1>{ value.title }</h1>
          <p>{ value.text }</p>
        </CarouselElement>
      ))

    }
  }

  renderDots() {
    if(this.state.dots) {

      return (
        <div className='carousel_dots'>
          {
            this.props.items.map((value, key) => (
              <CarouselDot
                key={key}
                className={
                  key+1 >= this.state.current &&
                  key+1 <= this.state.current + this.state.elemRender-1
                  ? 'carousel_dot active'
                  : 'carousel_dot'
                }
              />
            ))
          }
        </div>
      );

    };
  }

  renderArrows() {
    if(this.state.arrows) {

      return {
        left: <CarouselArrow className='carousel_arrow carousel_arrow_left' handle={this.handleArrowPrev} />,
        right: <CarouselArrow className='carousel_arrow carousel_arrow_right' handle={this.handleArrowNext} />
      };

    } else {

      return {
        left: undefined,
        right: undefined
      };

    };
  }

  render() {
    let styleInner = {};
    let styleWrapper = {};

    if('inner' in this.state.styles) styleInner = this.state.styles.inner;
    if('wrapper' in this.state.styles) styleWrapper = this.state.styles.wrapper;

    let content = this.renderContent();
    let dots = this.renderDots();
    let arrows = this.renderArrows();

    return (
      <div className='carousel'>
        <h1>{this.props.title}</h1>
        {arrows.left}
        {arrows.right}
        <div style={ styleInner } className='carousel_inner'>
          <div style={ styleWrapper } className={`carousel_wrapper carousel_${this.props.settings.animation}`}>
            { content }
          </div>
        </div>
        { dots }
      </div>
    )
  }
}

export default Carousel
