import React from 'react';
import './Carousel.css';

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {current: 1, translateX: 0, styles: {}}
  }

  next() {
    let new_current;
    let new_translateX;
    if(this.state.current <= this.props.items.length) {
      new_current = this.state.current+1;
      new_translateX = this.state.translateX - this.props.settings.style.width
    } else {
      new_current = 1;
      new_translateX = 0;
    }

    this.setState({current: new_current,translateX: new_translateX, styles: {transform: `translateX(-${new_translateX}px)`}})
  }

  componentDidMount() {
    this.interval = setInterval(this.next(), 2500);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    let style = {};
    let content;

    if(this.props.settings.style) {
      style = {width: `${this.props.settings.style.width}px`, height: `${this.props.settings.style.height}px`}
    }

    if(this.props.items.length > 0) {
      content = this.props.items.map((value, key) => (
        <div className='carousel_element' key={key} style={style}>
          <h1>{key+1}</h1>
        </div>
      ))
    }

    return (
      <div style={style} className='carousel_inner'>
        <div className='carousel_wrapper'>
          {content}
        </div>
      </div>
    )
  }
}

export default Carousel
